mapper.select:
select no, title,startDate,endDate,updateDate,hit
	from(
		select rownum rnum,  no, title,startDate,endDate,updateDate,hit
		from(
			select  no, title,startDate,endDate,updateDate,hit
		from notice

		order by updateDate desc
	)
)
where rnum between #{startRow} and #{endRow}

select no, title, startDate, endDate, updateDate, hit
from (
    select rownum rnum, no, title, startDate, endDate, updateDate, hit
    from (
        select no, title, startDate, endDate, updateDate, hit
        from notice 
        where startDate <= sysdate and trunc(sysdate) <= endDate

        order by updateDate desc
    )
)
where rnum between #{startRow} and #{endRow}

select no, title,startDate,endDate,updateDate,hit
	from(
		select rownum rnum,  no, title,startDate,endDate,updateDate,hit
		from(
			select  no, title,startDate,endDate,updateDate,hit
		from notice
		where startDate > sysDate
		order by updateDate desc
	)
)

select no, title, startDate, endDate, updateDate, hit
	from(
		select rownum rnum, no, title, startDate, endDate, updateDate, hit
		from(
			select no, title, startDate, endDate, updateDate, hit
			from notice
			where endDate + 1 < sysDate 
			order by updateDate desc
		)
	)

select no, title, content, startDate, endDate, writeDate, updateDate, hit
from notice
where no = #{no}
